sourcesContent:
import AccountService from "../service/authService";
import transporter from "../config/email";
import bcrypt from "bcrypt";
import crypto from "crypto";
class AccountController {
  // async login(req, res) {
  //   const { email, password } = req.body;
  //   if (!email || !password) {
  //     return res.status(400).send("Email and password are required");
  //   }
  //   try {
  //     const account = await AccountService.authenticate(email, password);
  //     if (account.error) {
  //       return res.render("login", { error: account.error });
  //     }

  //     // Xc nh role v chuyn hng tng ng
  //     const role = account.RoleID;
  //     req.session.userID = account.AccountID; // t userID vo session
  //     switch (role) {
  //       case 1:
  //         return res.redirect("/updatePassword");
  //       case 2:
  //         return res.redirect("/pageDentist");
  //       case 3:
  //         return res.redirect("/pageOwner");
  //       case 4:
  //         return res.redirect("/pageAdmin");
  //       default:
  //         return res.status(200).json({ message: "Login successfully", account });
  //     }
  //   } catch (err) {
  //     console.error("Error executing query:", err.stack);
  //     return res.status(500).send("Database query error");
  //   }
  // }
  async login(req, res) {
    const { email, password } = req.body;
    if (!email || !password) {
      return res.status(400).send("Email and password are required");
    }
    try {
      const account = await AccountService.authenticate(
        email,
        password
      );
      if (account.error) {
        return res.render("login", { error: account.error });
      }
      res.status(200).json({ message: "Login successfully" });
    } catch (err) {
      console.error("Error executing query:", err.stack);
      res.status(500).send("Database query error");
    }
  }

  async register(req, res) {
    const { username, password, email, phone } = req.body;
    const roleID = 1;
    const hashedPassword = bcrypt.hashSync(password, 10);
    const verificationToken = crypto.randomBytes(32).toString("hex");
    const verificationLink = `http://localhost:3000/verify?token=${verificationToken}`;
    try {
      if (!username || !password || !email || !phone) {
        return res.status(400).json({ message: "All fields are required" });
      }

      console.log("Hashed password:", hashedPassword);
      const mailOptions = {
        from: process.env.EMAIL_APP_GMAIL,
        to: email,
        subject: "Email Verification",
        text: `Please verify your email by clicking the following link: ${verificationLink}`,
        html: `<p>Please verify your email by clicking the following link: <a href="${verificationLink}">Verify Email</a></p>`,
      };
      console.log("Request body:", req.body);
      transporter.sendMail(mailOptions, (error, info) => {
        if (error) {
          res.status(500).send(error.toString());
        }
        res.status(200).send("A verification email has been sent to:", email);
      });
      const newAccount = await AccountService.createAccountCustomer({
        username,
        hashedPassword,
        phone,
        email,
        roleID,
        verificationToken,
      });
      console.log("Account create:", newAccount);
      if (newAccount) {
        res.redirect("/login");
      }
    } catch (err) {
      //console.error("Error inserting into the database:", err.stack);
      if (err.message === "Email already exists") {
        return res.render("register", {
          error: "Email already taken",
          username: username,
          email: email,
          phone: phone,
        });
      }
      if (err.message === "Username already exists") {
        return res.render("register", {
          error: "Username already taken",
          username: username,
          email: email,
          phone: phone,
        });
      }
      res.status(500).send("Database insert error");
    }
  }
  async showLogin(req, res) {
    res.render("login", {
      usernameOrEmail: "",
      password: "",
    });
  }
  async showRegister(req, res) {
    res.render("register", {
      error: null,
      username: "",
      password: "",
      email: "",
      phone: "",
    });
  }
  async verifyEmail(req, res) {
    const { token } = req.query;
    try {
      const account = await AccountService.getTokenVerify(token);
      if (!account) {
        return res.status(400).send("Invalid verification token");
      }
      res.status(200).send("Email has been successfully verified ");
    } catch (error) {
      res.status(500).send("Error verifying email: ", error.message);
    }
  }
  async updatePassword(req, res) {
    const { currentPassword, newPassword } = req.body;
    const AccountId = req.session.userID; // Assuming you have userId from session or JWT token

    if (!currentPassword || !newPassword) {
      return res
        .status(400)
        .json({ message: "Current and new password are required" });
    }

    try {
      const account = await AccountService.getAccountById(AccountId);
      if (!account) {
        return res.status(404).json({ message: "User not found" });
      }

      const isMatch = await bcrypt.compareSync(
        currentPassword,
        account.Password
      );
      console.log(isMatch);
      if (!isMatch) {
        return res
          .status(400)
          .json({ message: "Current password is incorrect" });
      }

      const hashedNewPassword = bcrypt.hashSync(newPassword, 10);
      account.Password = hashedNewPassword;
      await account.save();

      res.status(200).json({ message: "Password updated successfully" });
    } catch (error) {
      res
        .status(500)
        .json({ message: "Error updating password: " + error.message });
    }
  }

  async showupdatePassword(req, res) {
    res.render("updatePassword", {
      currentPassword: "",
      newPassword: "",
    });
  }
  async showForgotPassword(req, res) {
    res.render("forgotPassword");
  }
  async forgotPassword(req, res) {
    const { email } = req.body;
    console.log(email);
    const newAccount = await AccountService.getAccountByUserNameOrEmail(email);
    if (!newAccount) {
      return res
        .status(400)
        .json({ message: `Not found account with email ${email}` });
    }
    const verificationToken = crypto.randomBytes(32).toString("hex");
    const verificationLink = `http://localhost:5173/ResetPassword?token=${verificationToken}`;
    const account = await AccountService.createResetToken(
      email,
      verificationToken
    );
    if (!account) {
      console.log("Not found account in controller");
    }
    const mailOptions = {
      from: process.env.EMAIL_APP_GMAIL,
      to: email,
      subject: "Email ResetPassword",
      text: `Please resetPassword your account by clicking the following link: ${verificationLink}`,
      html: `<p>Please resetPassword your account by clicking the following link: <a href="${verificationLink}">Verify ResetPassword</a></p>`,
    };
    console.log("Request body:", req.body);
    transporter.sendMail(mailOptions, (error, info) => {
      if (error) {
        res.status(500).send(error.toString());
      }
      res.status(200).send(`A resetPassword email has been sent to: ${email}`);
    });
  }

  async showresetPassword(req, res) {
    const { token } = req.query;
    const account = await AccountService.getResetToken(token);
    if (!account) {
      res.status(400).json({ message: "Invalid reset token" });
    }
    const id = account.AccountID;
    res.status(200).json({ id });
  }

  async resetPassword(req, res) {
    const { password, confirmPassword, token } = req.body;
    if (password !== confirmPassword) {
      res
        .status(400)
        .json({ message: "Password and corfim Password not same" });
    }
    const hasedpassword = bcrypt.hashSync(password, 10);
    const account = await AccountService.saveNewPassword(token, hasedpassword);
    if (!account) {
      console.log(`Not found account with token ${token}`);
    }
    res.status(200).json({ message: "Reset password successfully" });
  }
  async showPageAdmin(req, res) {
    res.render("admin");
  }
  async addAccountFromAdmin(req, res) {
    const { username, password, email, phone, roleID } = req.body;
    const hashedPassword = bcrypt.hashSync(password, 10);
    try {
      const newAccount = await AccountService.createAccountDentistOrOwner({
        username,
        hashedPassword,
        phone,
        email,
        roleID,
      });
      if (newAccount) {
        res.status(200).json({ message: "Create Account Successfully" });
      }
    } catch (err) {
      if (err.message === "Email already exists") {
        return res.render("register", {
          error: "Email already taken",
          username: username,
          email: email,
          phone: phone,
        });
      }
      if (err.message === "Username already exists") {
        return res.render("register", {
          error: "Username already taken",
          username: username,
          email: email,
          phone: phone,
        });
      }
      res.status(500).send("Database insert error");
    }
  }
  async showOwnerPage(req, res) {
    res.render("owner");
  }
  async showDentistPage(req, res) {
    res.render("dentist");
  }

  async showSchedule(req, res) {
    res.render("schedule", {
      day: "",
      stime: "",
      etime: "",
    });
  }

  async schedule(req, res) {
    const { day, stime, etime, dentistId } = req.body; // m bo rng dentistId c ly t req.body
    const DentistID = req.session.userID || dentistId; // S dng dentistId t request body hoc session
    if (!DentistID) {
      return res.status(400).send("DentistID is required");
    }
    try {
      const newSchedule = await AccountService.createSchedule({
        DentistID,
        day,
        stime,
        etime
      });
      res.status(200).send("Schedule added successfully", newSchedule);
    } catch (error) {
      res.status(500).send("Error adding schedule: " + error.message);
    }
  }

  async handleGetAllUser(req, res) {

    let AccountID = req.query.AccountID; // All, id
    if (!AccountID) {
      return res.status(200).json({
        errCode: 1,
        errMessage: 'Missing required patameter',
        account: []
      });
    }
    let account = await AccountService.getAllUsers(AccountID);

    return res.status(200).json({
      errCode: 0,
      errMessage: 'OK',
      account
    });
  }

  async handleDeleteUser(req, res) {
    if (!req.body.AccountID) {
      return res.status(500).json({
        errCode: 1,
        errMessage: "Missing required parameter"
      })
    }
    let message = await AccountService.deleteUser(req.body.AccountID)
    return res.status(200).json(message)
  }

  async handleEditUser() {
    let data = req.body
    let message = await AccountService.updateUser(data);
    return res.status(200).json(message)
  }



}

export default new AccountController();

